from django.urls import pathfrom django.conf.urls import url,includefrom django.contrib.auth import views as auth_viewsfrom django.conf import settingsfrom django.conf.urls.static import staticfrom . import viewsfrom django.contrib.auth.decorators import login_required, permission_requiredfrom assets.views import (    AssetsView,    VehicleView,    BuildingView,    UserLogin    )app_name = "assets"urlpatterns =[    path('', login_required(AssetsView.as_view(template_name="index.html")), name='assets'),    path('vehicle', login_required(VehicleView.as_view(template_name="vehicle.html")), name='vehicle'),    path('building', login_required(BuildingView.as_view(template_name="building.html")), name='building'),    path('newpersonnel', login_required(views.newpersonnel), name='personnel'),    path('loadpersonnel', login_required(views.loadpersonnel), name='loadpersonnel'),    path('login', login_required(UserLogin.as_view(template_name="user_login.html")), name='login'),    path('images', login_required(views.showimage), name='showimage'),     path('staff/', include("users.urls")),    ] + static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)        