from django.urls import pathfrom datetime import datetimefrom django.conf.urls import url,includefrom django.contrib.auth import views as auth_viewsfrom . import viewsfrom django.conf import settingsfrom django.conf.urls.static import staticfrom django.contrib.auth.decorators import login_required, permission_requiredfrom client.views import (    ClientsView,    UserLogin)app_name = "client"urlpatterns =[    path('', login_required(ClientsView.as_view(template_name="index.html")), name='client'),    path("site/<slug:client_id>/", login_required(views.site), name ="site"),    path('login', login_required(UserLogin.as_view(template_name="user_login.html")), name='login'),    path("searchsite", login_required(views.searchsite), name ="searchsite"),    path('staff/', include("users.urls")),    ]+ static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)    