from django.urls import pathfrom django.conf.urls import url,includefrom datetime import datetimefrom django.contrib.auth import views as auth_viewsfrom . import viewsfrom django.conf import settingsfrom django.conf.urls.static import staticfrom django.contrib.auth.decorators import login_required, permission_requiredfrom locations.views import (    LocationView)app_name = "locations"urlpatterns =[     path('login/',         auth_views.LoginView.as_view         (             template_name='users/login.html',             extra_context=             {                 'title': 'Log in',                 'year' : datetime.now().year,             }         ),         name='user_login'),    path('', login_required(LocationView.as_view(template_name="index.html")), name='location'),    path("site/<slug:location_id>/", views.site, name ="site"),    path("searchsite", views.searchsite, name ="searchsite"),    path('staff/', include("users.urls")),    ]+ static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)    